import React, { useState } from 'react';
import axios from 'axios';
import swal from 'sweetalert';

const Formulario = () => { 

    const baseUrl="https://localhost:44330/api/mudanza";

    const [datos, setDatos] = useState({
        cedula : '',
        archivo : null
    })

    const handleInputChange =(event) => {
        setDatos({
           ...datos,
           cedula : event.target.value
        })
    }

    const handleInputChangeFile =(event) => {
        setDatos({
           ...datos,
           archivo : event.target.files[0]
        })
    }

    const mostrarAlert = (mensaje, tipo)=>{
        swal({
            title: "",
            text: mensaje === "" ? "Datos procesados correctamente" : mensaje,
            icon: tipo ? "error" : "success",
            button: "Aceptar"
        });
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        let formData = new FormData();
        formData.append('archivo', datos.archivo)
        formData.append('cedula', datos.cedula)    
    
        axios.post(baseUrl, formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
        .then(resp => {
            console.log(resp.data);
            mostrarAlert(resp.data.errorMensaje, resp.data.error);
            let blob = new Blob(resp.data.errorMensaje, {
                type: "text/plain"
            });

            descargarArchivo(blob, 'lazy_loading_output.txt');
            
        })
    }

    const descargarArchivo = (contenidoEnBlob, nombreArchivo) => {
        let reader = new FileReader();
        reader.onload = (event) => {
            let save = document.createElement('a');
            save.href = event.target.result;
            save.target = '_blank';
            save.download = nombreArchivo || 'archivo.dat';
            var clicEvent = new MouseEvent('click', {
                'view': window,
                'bubbles': true,
                'cancelable': true
            });
            save.dispatchEvent(clicEvent);
            (window.URL || window.webkitURL).revokeObjectURL(save.href);
        };
        reader.readAsDataURL(contenidoEnBlob);
    };
    
    return (
        <>
            <h1 className="text-center">Mudanzas</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Cédula</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        id="cedula" 
                        name="cedula" 
                        placeholder="Cédula"
                        onChange={handleInputChange}
                        required />
                </div>
                <div className="form-group">
                    <label>Archivo</label>
                    <input 
                        type="file" 
                        className="form-control" 
                        id="archivo" 
                        name="archivo" 
                        placeholder="Archivo" 
                        accept=".txt"
                        onChange={handleInputChangeFile}
                        required />
                </div>
                <button type="Submit" className="btn btn-primary" >Procesar</button>
            </form>
        </>
    );
}

export default Formulario;
