import React, { Fragment, useState } from 'react';
import axios from 'axios';



const Formulario = () => { 

    const baseUrl="https://localhost:44330/api/mudanza";

    const [datos, setDatos] = useState({
        cedula : '',
        archivo : null
    })

    const [datosResult, setDatosResult] = useState({
        error : false,
        ErrorMensaje : ''
    })

    const handleInputChange =(event) => {
        setDatos({
           ...datos,
           cedula : event.target.value
        })
    }

    const handleInputChangeFile =(event) => {
        setDatos({
           ...datos,
           archivo : event.target.files[0]
        })
    }

    const Post =async() => {
        await axios.post(baseUrl, datos).then(response=>{

            console.log(response.data);

            setDatosResult(datosResult.concat(response.data));

        }).catch(error=>{
            console.log(error);
        })
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        let formData = new FormData();
        formData.append('archivo', datos.archivo)
        formData.append('cedula', datos.cedula.value)    
    
        axios.post(baseUrl, formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
        .then(resp => {
            console.log(resp.status);
        })
    }

    
    return (
        <>
            <h1 className="text-center">Mudanzas</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label for="cedula">Cédula</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        id="cedula" 
                        name="cedula" 
                        placeholder="Cédula"
                        onChange={handleInputChange} />
                    <small id="cedulaHelp" className="form-text text-muted">Campo obligatorio</small>
                </div>
                <div className="form-group">
                    <label for="archivo">Archivo</label>
                    <input 
                        type="file" 
                        className="form-control" 
                        id="archivo" 
                        name="archivo" 
                        placeholder="Archivo" 
                        accept=".txt"
                        onChange={handleInputChangeFile} />
                </div>
                <button type="Submit" className="btn btn-primary" >Procesar</button>
            </form>
        </>
    );
}

export default Formulario;
