            console.log(error);
        })
    }

    
    return (
        <>
            <h1 className="text-center">Mudanzas</h1>
            <form>
                <div className="form-group">
                    <label for="cedula">Cédula</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        id="cedula" 
                        name="cedula" 
                        placeholder="Cédula"
                        onChange={handleInputChange} />
                    <small id="cedulaHelp" className="form-text text-muted">Campo obligatorio</small>
                </div>
                <div className="form-group">
                    <label for="archivo">Archivo</label>
                    <input 
                        type="file" 
                        className="form-control" 
                        id="archivo" 
