import React, { Fragment, useState } from 'react';
import axios from 'axios';
import swal from 'sweetalert';

const Formulario = () => { 

    const baseUrl="https://localhost:44330/api/mudanza";

    const [datos, setDatos] = useState({
        cedula : '',
        archivo : null
    })

    const handleInputChange =(event) => {
        setDatos({
           ...datos,
           cedula : event.target.value
        })
    }

    const handleInputChangeFile =(event) => {
        setDatos({
           ...datos,
           archivo : event.target.files[0]
        })
    }

    const mostrarAlert = (mensaje, tipo)=>{
        swal({
            title: "",
            text: mensaje == "" && "Datos procesados correctamente",
            icon: tipo ? "error" : "success",
            button: "Aceptar"
        });
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        let formData = new FormData();
        formData.append('archivo', datos.archivo)
        formData.append('cedula', datos.cedula.value)    
    
        axios.post(baseUrl, formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
        .then(resp => {
            console.log(resp.data);
            alert(JSON.stringify(resp.data))
            mostrarAlert(resp.data.errorMensaje, resp.data.error);
        })
    }

    
    return (
        <>
            <h1 className="text-center">Mudanzas</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Cédula</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        id="cedula" 
                        name="cedula" 
                        placeholder="Cédula"
                        onChange={handleInputChange} />
                    <small id="cedulaHelp" className="form-text text-muted">Campo obligatorio</small>
                </div>
                <div className="form-group">
                    <label>Archivo</label>
                    <input 
                        type="file" 
                        className="form-control" 
                        id="archivo" 
                        name="archivo" 
                        placeholder="Archivo" 
                        accept=".txt"
                        onChange={handleInputChangeFile} />
                </div>
                <button type="Submit" className="btn btn-primary" >Procesar</button>
            </form>
        </>
    );
}

export default Formulario;
